const richestCompanies=["APPLE","MICROSOFT","ARAMCO","ALPHABET (GOOGLE)","AMAZON","BERKSHIRE HATHAWAY","UNITEDHEALTH","JOHNSON & JOHNSON","TESLA","VISA"],draggableList=document.getElementById("draggable-list"),checkBtn=document.getElementById("check"),listItems=[];let dragStartIndex;function createList(){[...richestCompanies].map((e=>({value:e,sort:Math.random()}))).sort(((e,t)=>e.sort-t.sort)).map((e=>e.value)).forEach(((e,t)=>{const a=document.createElement("li");a.setAttribute("data-index",t),a.innerHTML=`\n      <span class="number">${t+1}</span>\n      <div class="draggable" draggable="true">\n        <p class="company-name">${e}</p>\n        <i class="fas fa-grip-lines"></i>\n      </div>\n    `,listItems.push(a),draggableList.appendChild(a)})),addEventListeners()}function addEventListeners(){const e=document.querySelectorAll(".draggable"),t=document.querySelectorAll(".draggable-list li");e.forEach((e=>{e.addEventListener("dragstart",dragStart)})),t.forEach((e=>{e.addEventListener("dragover",dragOver),e.addEventListener("drop",dragDrop),e.addEventListener("dragenter",dragEnter),e.addEventListener("dragleave",dragLeave)}))}function checkOrder(){listItems.forEach(((e,t)=>{e.querySelector(".draggable").innerText.trim()!==richestCompanies[t]?e.classList.add("wrong"):(e.classList.remove("wrong"),e.classList.add("right"))}))}function dragStart(){dragStartIndex=+this.closest("li").getAttribute("data-index")}function dragEnter(){this.classList.add("over")}function dragLeave(){this.classList.remove("over")}function dragOver(e){e.preventDefault()}function dragDrop(){const e=+this.getAttribute("data-index");swapItems(dragStartIndex,e),this.classList.remove("over")}function swapItems(e,t){const a=listItems[e].querySelector(".draggable"),r=listItems[t].querySelector(".draggable");listItems[e].appendChild(r),listItems[t].appendChild(a)}createList(),checkBtn.addEventListener("click",checkOrder);